  user_id: number;
  featured_link: string;
  pinned_globally: boolean;
  pinned_at: string;
  pinned_until: string;
  image_url: string;
  slow_mode_seconds: number;
  draft: string;
  draft_key: string;
  draft_sequence: number;
  unpinned: string;
  pinned: boolean;
  current_post_number: number;
  highest_post_number: number;
